	$headers .= "Content-Type: text/html; charset=ISO-8859-1" . "\n";
	$headers .= "From: no-reply@associatedemployers.org" . "\n";
	/* eob */
	
	if($type == "neworder") {
		mail($email, "Webinar Order Information - Associated Employers", $newOrderMessageClient, $headers); //client email
		mail($administrativeEmail, "Webinar Order - Associated Employers", $newOrderMessageAdmin, $headers); //admin email
	}
	
	$results = array(
		'success' => 'true',
		'errors' => $errors,
		'email' => $email
		);
	
	echo json_encode($results);
?>